if test -z $1; then exit 0; fi

# Si on a 6 boutons
for player in 1 2; do
  if [[ "$1" =~ ^arcade-manufacturer.* ]];then
    typeset -n arr="astrocityp${player}"
    echo "Using astrocity colors"
  else
    typeset -n arr=$1
    if test -z "$arr"; then
      typeset -n arr="astrocityp${player}"
      echo "Using astrocity colors"
    else
      echo "Using ${1} colors"
    fi
  fi

  i=1
  # Basic config
  #  1  2  3
  #  4  5  6
  # Arrays = (1 2 3 4 5 6 7 8 SELECT START HK)
  # So we need to map like that:
  #  3  4  5
  #  1  2  6
  for button in 3 4 5 1 2 6 7 8; do
    for led in 1 2; do
      echo ${arr[$((button - 1))]} > "/sys/class/leds/aio_p${player}_b${i}_${led}/multi_intensity"
    done
    i=$((i + 1))
  done
  i=8
  for button in "select" "start"; do
    for led in 1 2; do
      echo ${arr[$i]} > "/sys/class/leds/aio_p${player}_${button}_${led}/multi_intensity"
    done
    i=$((i+1))
  done

  if [ "${player}" == "1" ];then
    for led in 1 2; do
      echo ${arr[10]} > "/sys/class/leds/aio_hotkey_${led}/multi_intensity"
    done
  fi

done
